import React, { Component } from 'react'
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import ProductService from '../task/Test JSON.json';
import { Dialog } from 'primereact/dialog';
import { Button } from 'primereact/button';
import { InputText } from 'primereact/inputtext';
import { Calendar } from 'primereact/calendar';


export default class Task extends Component {
    constructor(props) {
        super(props);
        this.state = {
            products: null,
            socialMediaList : [] = [],
            selectedrow: []=[],
            displayBasic: Boolean = false,
            date1 : null
        };
        // this.productService = new ProductService();
        this.displayBasic = this.onClick.bind(this);

    }

    componentDidMount() {
        // this.productService.getProductsSmall().then(data => this.setState({ products: data }));
        // this.socialMediaList = ProductService.members;
    }
    footer = (
        <div>
            {/* <Button label="Yes" icon="pi pi-check" onClick={this.onHide} />
            <Button label="No" icon="pi pi-times" onClick={this.onHide} /> */}
        </div>
    );
     myIcon = (
        <button className="p-dialog-titlebar-icon p-link">
            <span className="pi pi-search"></span>
        </button>
    )
    onClick(){
        this.state.displayBasic=true;
    }
    onHide(name) {
        this.setState({
            displayBasic : false
        });
    }
    renderFooter(name) {
        return (
            <div>
                <Button label="No" icon="pi pi-times" onClick={() => this.onHide()} className="p-button-text" />
                <Button label="Yes" icon="pi pi-check" onClick={() => this.onHide()} autoFocus />
            </div>
        );
    }
    selected_date(picked){
        let pick = picked.target.value
        let pickedd = pick.toGMTString().slice(4).split(' ').slice(0, 4).join(' ');
        if(this.state.selectedrow.activity_periods.length  > 0 ){
           for(let i=0 ; i < this.state.selectedrow.activity_periods.length ; i++){
            // let Get_date = this.state.selectedrow.activity_periods[i].start_time;
            // let conv_get_date = Get_date.slice(5).split(' ').slice(0, 4).join(' ')
            console.log("pickedd",pickedd);
                if(this.state.selectedrow.activity_periods[i].start_time.slice(5).split(' ').slice(0, 4).join(' ') == pickedd){
                   console.log("true");
                   {

                   }
                }
                else {
                   console.log("false");
                }
           }
        }
        else {
            alert("No Activity Record Found");
        }
    }
    render() {
        this.socialMediaList = ProductService.members;

        console.log("this.socialMediaList",this.state.selectedrow);
        return (
            <div className="p-4">
            <div className="card">
                <DataTable value={this.socialMediaList} header="Employees List" footer="Footer" className="p-datatable-gridlines" selectionMode="single"
                    selection={this.state.selectedrow} onSelectionChange={e => this.setState({selectedrow: e.value}
                        ,this.onClick('displayBasic'))} >
                    <Column field="id"  header="ID"></Column>
                    <Column field="real_name" header="Name"></Column>
                    {/* <Column field="category" header="Category"></Column>
                    <Column field="quantity" header="Quantity"></Column> */}
                </DataTable>
            </div>
            <Dialog header={this.state.selectedrow.real_name} visible={this.state.displayBasic} style={{ width: '70vw' }} maximizable="displayBasic" maximized="displayBasic" footer={this.renderFooter()} onHide={() => this.onHide()}>
                <div className="row p-3">
                    <div className="col-sm-4 text-left">
                        Employee ID :
                        <div className="pl-1">
                         <InputText id="" value={this.state.selectedrow.id} disabled/>
                        </div>
                    </div>
                    <div className="col-sm-4 text-left">
                        Name :
                        <div className="pl-1">
                          <InputText id="" value={this.state.selectedrow.real_name} disabled/>
                       </div>
                    </div>
                    <div className="col-sm-4 text-left">
                        Location :
                        <div className="pl-1">
                          <InputText id="" value={this.state.selectedrow.tz} disabled/>
                       </div>
                    </div>
                </div>
                <div className="row p-3">
                    <div className="col-sm-4 text-left">
                        Pick a Date :
                        <div className="pl-1">
                        {/* <input class="form-control" type="date" placeholder="Date" dateformat="dd/mm/yyyy" value={this.state.date1} onChange={(e)=> this.selected_date(e)}  /> */}
                        <Calendar id="basic" value={this.state.date1}      onChange={(e)=> this.selected_date(e)}/>
                        </div>
                    </div> 
                    <div className="col-sm-4">
                        <DataTable value={this.socialMediaList} header="Activity Log" footer="Footer" className="p-datatable-gridlines" selectionMode="single" >
                            <Column field="id"  header="ID"></Column>
                            <Column field="real_name" header="Name"></Column>
                            {/* <Column field="category" header="Category"></Column>
                            <Column field="quantity" header="Quantity"></Column> */}
                         </DataTable>
                    </div>
                </div>
            </Dialog>
        </div>
        )
    }
}
