import React, { Component } from 'react'
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
// import ProductService from '../service/ProductService';
import ProductService from '../task/Test JSON.json';
import { Dialog } from 'primereact/dialog';


export default class Task extends Component {
    constructor(props) {
        super(props);
        this.state = {
            products: null,
            socialMediaList : [] = [],
            selectedID: null

        };
        // this.productService = new ProductService();
    }

    componentDidMount() {
        // this.productService.getProductsSmall().then(data => this.setState({ products: data }));
        // this.socialMediaList = ProductService.members;
    }
    footer = (
        <div>
            {/* <Button label="Yes" icon="pi pi-check" onClick={this.onHide} />
            <Button label="No" icon="pi pi-times" onClick={this.onHide} /> */}
        </div>
    );
     myIcon = (
        <button className="p-dialog-titlebar-icon p-link">
            <span className="pi pi-search"></span>
        </button>
    )
    onClick(name, position) {
        let state = {
            [`${name}`]: true
        };

        if (position) {
            state = {
                ...state,
                position
            }
        }

        this.setState(state);
    }
    render() {
        this.socialMediaList = ProductService.members;

        console.log("this.socialMediaList",this.state.selectedID);
        return (
            <div class="p-4">
            <div className="card">
                <DataTable value={this.socialMediaList} header="Employees List" footer="Footer" className="p-datatable-gridlines" selectionMode="single"
                    selection={this.state.selectedID} onSelectionChange={e => this.setState({selectedID: e.value.id})}
                    onClick={() => this.onClick('displayBasic')} >

                    <Column field="id"  header="ID"></Column>
                    <Column field="real_name" header="Name"></Column>
                    {/* <Column field="category" header="Category"></Column>
                    <Column field="quantity" header="Quantity"></Column> */}
                </DataTable>
            </div>
            <Dialog header="Header Text" footer={this.footer} icons={this.myIcon} visible={this.state.visible} style={{width: '50vw'}} modal onHide={this.onHide}>
                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.
                Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat
                cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p>
            </Dialog>

        </div>
        )
    }
}
